(()=>{const e=[],n=document.querySelector("input"),t=document.querySelector("textarea"),a=document.querySelector(".addFeedback");document.querySelector("button").addEventListener("click",(()=>{n.value.length>0&&t.value.length>0?(e.push({name:n.value,feedback:t.value}),a.innerHTML="",e.forEach((e=>{a.innerHTML+=`\n                <div class="block">\n                <p class="name">${e.name}</p>\n                <p class="description">${e.feedback}</p>\n                </div>\n                `}))):alert("Please enter something")}))})();